import org.junit.Test;
import static org.junit.Assert.*;

public class DigitRotationTest {
	
	@Test(timeout=2000)
	public void test0() {
		String X = "123";
		assertEquals(312, new DigitRotation().sumRotations(X));
	}
	
	@Test(timeout=2000)
	public void test1() {
		String X = "3570";
		assertEquals(10407, new DigitRotation().sumRotations(X));
	}
	
	@Test(timeout=2000)
	public void test2() {
		String X = "5545";
		assertEquals(21208, new DigitRotation().sumRotations(X));
	}
	
	@Test(timeout=2000)
	public void test3() {
		String X = "1283749218734901238749213847902";
		assertEquals(867286291, new DigitRotation().sumRotations(X));
	}
	
	@Test(timeout=2000)
	public void test4() {
		String X = "";
		assertEquals(0, new DigitRotation().sumRotations(X));
	}
}
